import { createSlice } from "@reduxjs/toolkit";

const initialState = false;

const cartOpenSlice = createSlice({
    name: 'cartOpen',
    initialState,
    reducers: {
        cartOpen: (state) => {
            return !state.cartOpen; 
        },
    }
});

export const { cartOpen } = cartOpenSlice.actions;

export default cartOpenSlice.reducer;
