import React, { useEffect } from 'react';
import ProductItem from '../ProductItem';
import { useQuery } from '@apollo/client';
import { QUERY_PRODUCTS } from '../../utils/queries';
import { idbPromise } from '../../utils/helpers';
import spinner from '../../assets/spinner.gif';
import { shallowEqual, useDispatch, useSelector } from 'react-redux';
import { updateProducts } from '../../reducers/products/productSlice';

function ProductList() {

  const { loading, data } = useQuery(QUERY_PRODUCTS);
  const dispatch = useDispatch();
  const storeObj = useSelector((store) => store, shallowEqual);
  const currentCategory = storeObj.currentCategory;

  useEffect(() => {
    if (data) {
      dispatch(updateProducts(data.products));
      data.products.forEach((product) => {
        idbPromise('products', 'put', product);
      });
    } else if (!loading) {
      idbPromise('products', 'get').then((products) => {
        dispatch(updateProducts(data.products));
      });
    }
  }, [data, loading]);

  function filterProducts() {
    debugger;
    if (currentCategory && Object.keys(currentCategory).length === 0) {
      return storeObj.products;
    }

    return storeObj.products.filter(
      (product) => product.category._id === currentCategory
    );
    // return products;
  }

  return (
    <div className="my-2">
      <h2>Our Products: </h2>
      {storeObj.products?.length ? (
        <div className="flex-row">
          {filterProducts().map((product) => (
            <ProductItem
              key={product._id}
              _id={product._id}
              image={product.image}
              name={product.name}
              price={product.price}
              quantity={product.quantity}
            />
          ))}
        </div>
      ) : (
        <h3>You haven't added any products yet!</h3>
      )}
      {loading ? <img src={spinner} alt="loading" /> : null}
    </div>
  );
}

export default ProductList;
