import { createSlice } from "@reduxjs/toolkit";

const initialState = false;

const cartOpenSlice = createSlice({
    name: 'cartOpen',
    initialState,
    reducers: {
        toogleCart: (state) => {
            debugger;
            return !state.cartOpen; 
        },
    }
});

export const { toogleCart } = cartOpenSlice.actions;

export default cartOpenSlice.reducer;
